
create database vacationDB;
use vacationdb;

CREATE TABLE users
(
id int auto_increment,
firstName varchar(255),
lastName varchar(255),
username varchar(255),
password varchar(1000),
isAdmin boolean default 0,
primary key (id),
UNIQUE KEY uniqe_username (username)
);

